import{_ as r,c as a,a0 as t,o}from"./chunks/framework.D9MR4Zbk.js";const c=JSON.parse('{"title":"Windows 软件","description":"","frontmatter":{},"headers":[],"relativePath":"blog/windows/software.md","filePath":"blog/windows/software.md"}'),l={name:"blog/windows/software.md"};function n(i,e,h,s,p,f){return o(),a("div",null,e[0]||(e[0]=[t('<h1 id="windows-软件" tabindex="-1">Windows 软件 <a class="header-anchor" href="#windows-软件" aria-label="Permalink to &quot;Windows 软件&quot;">​</a></h1><h2 id="系统镜像" tabindex="-1">系统镜像 <a class="header-anchor" href="#系统镜像" aria-label="Permalink to &quot;系统镜像&quot;">​</a></h2><ul><li><a href="https://github.com/Atlas-OS/Atlas" target="_blank" rel="noreferrer">Atlas-OS/Atlas</a>，Windows 修改版，增强隐私，优化性能，移除广告</li><li><a href="https://github.com/meetrevision/playbook" target="_blank" rel="noreferrer">ReviOS</a>，搭配 Playbook 系统修改器，实现一系列自动优化、精简</li></ul><h2 id="提醒" tabindex="-1">提醒 <a class="header-anchor" href="#提醒" aria-label="Permalink to &quot;提醒&quot;">​</a></h2><p><a href="http://www.fadetop.com/" target="_blank" rel="noreferrer">FadeTop</a>：提醒休息，很老的软件但是支持最新系统，晃动鼠标可以快速退出休息</p><h2 id="生产力" tabindex="-1">生产力 <a class="header-anchor" href="#生产力" aria-label="Permalink to &quot;生产力&quot;">​</a></h2><p><a href="https://github.com/ShareX/ShareX" target="_blank" rel="noreferrer">ShareX</a>：开源截图工具，支持长截图、标注等，不支持贴图。</p><p><a href="https://pixpinapp.com/" target="_blank" rel="noreferrer">PixPin</a>：截图贴图（类似 Snipaste），支持长截图、文字识别、GIF 录制等。</p><p><a href="https://www.screentogif.com/" target="_blank" rel="noreferrer">ScreenToGif</a>：录制 GIF，可导出视频</p><p><a href="https://www.u.tools/" target="_blank" rel="noreferrer">uTools</a>：工具集（类似 Quicker），支持搜索、翻译、OCR、插件市场等</p><p><a href="https://truehumandesign.se/s_diffinity.php" target="_blank" rel="noreferrer">Diffinity</a>：文件夹各文件差异对比</p><p><a href="https://www.scootersoftware.com/home" target="_blank" rel="noreferrer">Beyond Compare</a>：文件对比工具</p><p><a href="https://reqable.com/en-US/" target="_blank" rel="noreferrer">Reqable</a>：api 请求、抓包调试工具</p><h2 id="开发" tabindex="-1">开发 <a class="header-anchor" href="#开发" aria-label="Permalink to &quot;开发&quot;">​</a></h2><p><a href="https://code.visualstudio.com/" target="_blank" rel="noreferrer">Visual Studio Code</a>：编辑器，支持各种语言，插件丰富，启动快</p><p><a href="https://starship.rs/zh-cn/" target="_blank" rel="noreferrer">Starship</a>：终端提示，配合 Nerd Font 使用，对终端启动速度影响小</p><h2 id="文档" tabindex="-1">文档 <a class="header-anchor" href="#文档" aria-label="Permalink to &quot;文档&quot;">​</a></h2><p><a href="https://github.com/sumatrapdfreader/sumatrapdf" target="_blank" rel="noreferrer">Sumatra</a>：PDF 阅读器，速度快，标注能力等于没有</p><p><a href="https://github.com/koodo-reader/koodo-reader" target="_blank" rel="noreferrer">Koodo Reader</a>: PDF 阅读器，速度中等，标注能力中下</p><p><a href="https://www.onlyoffice.com/zh/download-desktop.aspx" target="_blank" rel="noreferrer">ONLYOFFICE Desktop Editors</a>：office 替代品</p><p><a href="https://otp.landian.vip/zh-cn/" target="_blank" rel="noreferrer">Office Tool Plus</a>：一键部署 Microsoft Office</p><h2 id="多媒体" tabindex="-1">多媒体 <a class="header-anchor" href="#多媒体" aria-label="Permalink to &quot;多媒体&quot;">​</a></h2><p><a href="https://imageglass.org/" target="_blank" rel="noreferrer">ImageGlass</a>：图片查看，支持查看当前文件夹、一键设为壁纸和锁屏（不需要自己缩放壁纸）</p><p><a href="https://mpv.io/" target="_blank" rel="noreferrer">mpv</a>，开源跨平台媒体播放器，<a href="https://www.tok9.com/archives/511/" target="_blank" rel="noreferrer">快捷键，必看</a>，<a href="https://github.com/mpv-player/mpv/wiki/Applications-using-mpv" target="_blank" rel="noreferrer">Applications using mpv</a></p><p><a href="https://www.videolan.org/vlc/" target="_blank" rel="noreferrer">VLC</a>：开源多媒体播放器</p><p><a href="https://github.com/qier222/YesPlayMusic" target="_blank" rel="noreferrer">YesPlayMusic</a>：网易云音乐第三方客户端，高颜值</p><h2 id="下载" tabindex="-1">下载 <a class="header-anchor" href="#下载" aria-label="Permalink to &quot;下载&quot;">​</a></h2><p><a href="https://github.com/GopeedLab/gopeed" target="_blank" rel="noreferrer">Gopeed</a>：磁力、种子下载，支持自动更新 tracker list</p><h2 id="其他" tabindex="-1">其他 <a class="header-anchor" href="#其他" aria-label="Permalink to &quot;其他&quot;">​</a></h2><p><a href="https://github.com/emoacht/Monitorian" target="_blank" rel="noreferrer">Monitorian</a>：开源屏幕亮度调节工具，支持多屏幕同时调节</p><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2><ul><li><a href="https://www.wxy97.com/archives/20" target="_blank" rel="noreferrer">我的常用软件 | WXY</a></li></ul>',32)]))}const b=r(l,[["render",n]]);export{c as __pageData,b as default};
